import React, { useState } from 'react'
import './App.css';
import Header from './Header';

function App() {
  let counter = useState(0);

  function increment() {
    counter ++;
    console.log(counter)
  }

  return (
    /**
      tittle é acessado pelo componente através do parametro props
    */
    <>
      <Header tittle={`Contador: ${counter}`}/>
      <button onClick={increment}>Increntar</button>
    </>
    );
}

export default App;
